<?php include_once "db.php";function get_type(){    $db = Watany::getInstance();    $mysqli = $db->getConnection();    $mysqli->query("SET NAMES 'utf8';");    $get_typeSql = $mysqli->query("SELECT * FROM types");    $x = array();    while ($row = $get_typeSql->fetch_assoc()) {        $x[] = $row;    }    $return = array();    $return['result'] = true;    $return['records'] = $x;    $mysqli->close();    return $return;}function type_add(){    $db = Watany::getInstance();    $mysqli = $db->getConnection();    $mysqli->query("SET NAMES 'utf8';");    $type_name = $_POST['name'];    $type_description = isset($_POST['description']) ? $_POST['description'] : NULL;    $type_addSql = $mysqli->query("INSERT INTO types( name, description , created_at) VALUES                                              ('$type_name','$type_description', now())");    $return = array();    $return['result'] = true;    $return['masseges'] = "تم الإضافة بنجاح";    $mysqli->close();    return $return;}function type_edit(){    $db = Watany::getInstance();    $mysqli = $db->getConnection();    $mysqli->query("SET NAMES 'utf8';");    $type_id = intval($_POST['id']);    $type_name = $_POST['name'];    $type_description = isset($_POST['description']) ? $_POST['description'] : NULL;    $type_editSql = $mysqli->query("UPDATE types SET                                                           name = '$type_name',                                                          description = '$type_description',                                                          WHERE id =$type_id                                                           ");    $return = array();    $return['result'] = true;    $return['masseges'] = "تم التعديل بنجاح";    $mysqli->close();    return $return;}function type_delete(){    $db = Watany::getInstance();    $mysqli = $db->getConnection();    $mysqli->query("SET NAMES 'utf8';");    $type_id = intval($_POST['id']);    $type_deleteSql = $mysqli->query("DELETE FROM types WHERE id = $type_id");    $return = array();    $return['result'] = true;    $return['masseges'] = "تم الحذف بنجاح";    $mysqli->close();    return $return;}?>